import React, { useState } from "react";
import "../styles/Blog.css";
import blogService from "../services/blogService";

const Blog = ({ blog }) => {
  const [showFull, setShowFull] = useState(false);

  const blogStyle = {
    paddingTop: 10,
    paddingBottom: 10,
    border: "solid",
    marginTop: 5,
    marginBottom: 5,
  };
  if (!showFull) {
    return (
      <div className="Blog">
        {blog.title} {blog.author}
        <button onClick={() => setShowFull(!showFull)}>view</button>
      </div>
    );
  } else {
    return (
      <div className="Blog">
        {blog.title}{" "}
        <button onClick={() => setShowFull(!showFull)}>hide</button>
        <p>{blog.url}</p>
        <p>
          {blog.likes}{" "}
          <button onClick={blogService.likeBlog(blog)}>like</button>
        </p>
        <p>{blog.author}</p>
      </div>
    );
  }
};

export default Blog;
