import React, { useState } from "react";
const Blog = ({ blog }) => {
  const [showFull, setShowFull] = useState(false);

  if (!showFull) {
    return (
      <div>
        {blog.title} {blog.author}
        <button onClick={() => setShowFull(!showFull)}>view</button>
      </div>
    );
  } else {
    <div>
      {blog.title}
      {blog.url}
      {blog.likes} <button>like</button>
      {blog.author}
    </div>;
  }
};

export default Blog;
