import React, { useState } from "react";
const Blog = ({ blog }) => {
  const [showFull, setShowFull] = useState(false);

  return (
    <div>
      {blog.title} {blog.author}{" "}
      <button onClick={() => setShowFull(!showFull)}>view</button>
    </div>
  );
};

export default Blog;
